    -- Secondary Dialog for Shape Options (Geometric Two Dimensional Shapes)

        function geometricTwoDimensional()
          app.openDialog("Geometric Two Dimensional Shapes", {"Arrowhead", "Triangle", "Equilateral Triangle", "Right Angle Triangle", "Back" }, "geometricTwoDimensionalCallback")
        end

            -- Callback for the Shape-1 Options Dialog (Geometric Two Dimensional Shapes)
               function geometricTwoDimensionalCallback(result)
                  if result == 1 then
                       qshape_Arrowhead()
                     elseif result == 2 then
                       qshape_Triangle()
                      elseif result == 3 then
                        qshape_Equi_Triangle()
                      elseif result == 4 then
                        qshape_RightAngleTriangle()
                      elseif result == 5 then
                        showMainShapeDialog()  -- Return to the main shape dialog
                  end
               end

  
    -- All Strokes for Geometric-2D Shapes

        -- Callback for inserting arrowhead
            function qshape_Arrowhead()
              local arrowhead = {
                x = { centerX - 5, centerX + 5, centerX - 5 },
                y = { centerY - 10, centerY - 5, centerY },
                width = 2.26, 
                tool = "pen", 
                color = 0xff0000,
                capStyle = "round",
                lineStyle = "solid",
              }
            
              app.addStrokes { strokes = { arrowhead } }
              app.refreshPage()
            end
    
        -- Callback for inserting triangle
            function qshape_Triangle()
              local height = math.sqrt(3) * 50  -- Height calculation for an equilateral triangle with a base of 100
            
              local equilateralTriangle = { 
                x = { centerX - 50, centerX + 50, centerX + 100, centerX - 50 },  -- Repeating the first point to close the triangle
                y = { centerY + (height / 2), centerY + (height / 2), centerY - (height / 2), centerY + (height / 2) },  -- Adjusted y-coordinates for height
                width = 2.26, 
                tool = "pen", 
                color = 0xff0000,
                lineStyle = "solid",
              }
              app.addStrokes { strokes = { equilateralTriangle } }
              app.refreshPage()
            end
            
        -- Callback for inserting triangle
            function qshape_Equi_Triangle()
              local height = math.sqrt(3) * 50  -- Height calculation for an equilateral triangle with a base of 100
            
              local equilateralTriangle = { 
                x = { centerX - 50, centerX + 50, centerX, centerX - 50 },  -- Repeating the first point to close the triangle
                y = { centerY + (height / 2), centerY + (height / 2), centerY - (height / 2), centerY + (height / 2) },  -- Adjusted y-coordinates for height
                width = 2.26, 
                tool = "pen", 
                color = 0xff0000,
                capStyle = "round",
                lineStyle = "solid",
              }

                app.addStrokes { strokes = { equilateralTriangle } }
                app.refreshPage()
            end      

        -- Callback for inserting right-angle isosceles triangle
            function qshape_RightAngleTriangle()
              local rightAngleTriangle = {
                x = { centerX + 50, centerX - 50, centerX - 50, centerX + 50 },  
                y = { centerY + 50, centerY + 50, centerY - 50, centerY + 50 },  
                width = 2.26, 
                tool = "pen", 
                color = 0x0000ff,  
                capStyle = "round",
                lineStyle = "solid",
              }
            
              app.addStrokes { strokes = { rightAngleTriangle } }
              app.refreshPage()
            end

