                app.addStrokes { strokes = { xAxis, yAxis, rightArrow, leftArrow, upArrow, downArrow } }  -- Then the axes and arrowheads
                app.refreshPage()
              end

      -- Callback for option-5 (full axis with Grids) 
        
              function GraphGrids()
                  -- Draw Grid lines
                  local gridLines = {}

                  for x = centerX - 28.3464566929 * 5, centerX + 28.3464566929 * 5, gridSpacing do
                    if x ~= centerX then  -- Skip the main Y-axis line
                      gridLines[#gridLines + 1] = {
                        x = { x, x },
                        y = { centerY - axisLength, centerY + axisLength },
                        width = gridWidth,
                        tool = "pen",
                        color = gridColor,
                        capStyle = "round",
                        lineStyle = "dot",
                      }
                    end
                  end
                
                  for y = centerY - 28.3464566929 * 5, centerY + 28.3464566929 * 5, gridSpacing do
                    if y ~= centerY then  -- Skip the main X-axis line
                      gridLines[#gridLines + 1] = {
                        x = { centerX - axisLength, centerX + axisLength },
                        y = { y, y },
                        width = gridWidth,
                        tool = "pen",
                        color = gridColor,
                        capStyle = "round",
                        lineStyle = "dot",
                      }
                    end

                   app.addStrokes { strokes = gridLines }
                   app.addStrokes { strokes = { xAxis, yAxis, rightArrow, leftArrow, upArrow, downArrow } }  -- first the axis then the arrowheads
                   app.refreshPage()
                 end
             end



